import React, {useState} from 'react'
import Login from "./components/login";
import Home from "./components/home";

function App() {
  const [conected, setConected] = useState(false);
  const setLocalStorage = (value) => {
    try {
      setConected(value);
      window.localStorage.setItem(conected, value);
    } catch{
      console.error
    }
  }
  const acceder =(state)=>{
    setConected(state)

  }
  return (
    conected ? <Home /> : <Login acceder={acceder} /> 
  );
}

export default App;
